import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert, Tabs, Tab, Table, Badge } from 'react-bootstrap';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import AuthContext from '../context/AuthContext';
import './ErranderDashboard.css';

// ... (StatCard, AvailableJobCard, MyTaskItem components remain the same)
const StatCard = ({ icon, title, value, colorClass }) => ( <div className="stat-card h-100"><div className={`icon ${colorClass}`}>{icon}</div><h4 className="fw-bold">{value}</h4><p className="text-muted">{title}</p></div> );
const AvailableJobCard = ({ task, onApply }) => ( <Card className="mb-3 shadow-sm"><Card.Header as="h5">{task.service.name}</Card.Header><Card.Body><Card.Text>{task.description}</Card.Text><Card.Text><strong>Est. Payout:</strong> ${task.estimatedPrice}</Card.Text><Button variant="success" onClick={() => onApply(task._id)}>Apply</Button></Card.Body></Card> );
const MyTaskItem = ({ task, onUpdateStatus }) => ( <Card className="mb-3 shadow-sm"><Card.Header className="d-flex justify-content-between align-items-center"><span>Task for User: ...{task.user._id.slice(-6)}</span><span className="fw-bold text-primary text-capitalize">{task.status.replace('_', ' ')}</span></Card.Header><Card.Body><Card.Text><strong>Description:</strong> {task.description}</Card.Text>{task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdateStatus(task._id, 'in_progress')}>Start Task</Button>}{task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdateStatus(task._id, 'completed')}>Mark as Complete</Button>}</Card.Body></Card> );


const ErranderDashboard = () => {
    const { token } = useContext(AuthContext);
    const [summary, setSummary] = useState({ walletBalance: '0.00', jobsCompleted: 0, rating: '5.0' });
    const [pendingTasks, setPendingTasks] = useState([]);
    const [myTasks, setMyTasks] = useState([]);
    const [paymentHistory, setPaymentHistory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchData = useCallback(async () => {
        if (!token) return;
        console.log("--- [Dashboard] Step 1: Starting data fetch ---");
        setLoading(true);
        try {
            console.log("--- [Dashboard] Step 2: Fetching summary... ---");
            const summaryRes = await erranderService.getDashboardSummary(token);
            setSummary(summaryRes.data);
            console.log("--- [Dashboard] Step 3: Summary fetched successfully. ---", summaryRes.data);

            console.log("--- [Dashboard] Step 4: Fetching pending tasks... ---");
            const pendingRes = await taskService.getPendingTasks(token);
            setPendingTasks(pendingRes.data);
            console.log("--- [Dashboard] Step 5: Pending tasks fetched successfully. ---", pendingRes.data);
            
            console.log("--- [Dashboard] Step 6: Fetching assigned tasks (My Jobs)... ---");
            const myTasksRes = await taskService.getAssignedTasks(token);
            setMyTasks(myTasksRes.data);
            console.log("--- [Dashboard] Step 7: Assigned tasks fetched successfully. ---", myTasksRes.data);

            console.log("--- [Dashboard] Step 8: Fetching payment history... ---");
            const historyRes = await taskService.getPaymentHistory(token);
            setPaymentHistory(historyRes.data);
            console.log("--- [Dashboard] Step 9: Payment history fetched successfully. ---", historyRes.data);

        } catch (err) {
            console.error("--- [Dashboard] CRITICAL ERROR during fetch: ---", err);
            setError('Failed to load dashboard data. Check the console for more details.');
        } finally {
            console.log("--- [Dashboard] Step 10: Fetch complete. Setting loading to false. ---");
            setLoading(false);
        }
    }, [token]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleApplyForTask = async (taskId) => { /* ... same as before ... */ };
    const handleUpdateStatus = async (taskId, status) => { /* ... same as before ... */};

    if (loading) return <Container className="text-center mt-5"><Spinner /></Container>;

    return (
        <div className="dashboard-container p-lg-4 p-md-3 p-2">
            <h2 className="mb-4 fw-bold">Errander Dashboard</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            
            <Row className="mb-4 g-3">
                <Col md={4}><StatCard icon="ðŸ’°" title="Wallet" value={`$${summary.walletBalance}`} colorClass="wallet-icon" /></Col>
                <Col md={4}><StatCard icon="ðŸ“¦" title="Jobs Completed" value={summary.jobsCompleted} colorClass="jobs-icon" /></Col>
                <Col md={4}><StatCard icon="â­" title="Your Rating" value={summary.rating} colorClass="rating-icon" /></Col>
            </Row>

            <div className="tasks-section">
                {/* ... The rest of the JSX is the same ... */}
                 <Tabs defaultActiveKey="available" id="errander-tabs" className="mb-3" justify>
                    <Tab eventKey="available" title={<>Available Jobs <Badge bg="success">{pendingTasks.length}</Badge></>}><div className="p-2">{pendingTasks.length > 0 ? pendingTasks.map(task => <AvailableJobCard key={task._id} task={task} onApply={handleApplyForTask} />) : <Alert variant="light">No jobs available.</Alert>}</div></Tab>
                    <Tab eventKey="my-tasks" title={<>My Jobs <Badge bg="primary">{myTasks.filter(t => t.status !== 'completed' && t.status !== 'paid').length}</Badge></>}><div className="p-2">{myTasks.length > 0 ? myTasks.map(task => <MyTaskItem key={task._id} task={task} onUpdateStatus={handleUpdateStatus} />) : <Alert variant="light">You have no assigned jobs.</Alert>}</div></Tab>
                    <Tab eventKey="payments" title="Payment History"><div className="p-2"><Table striped bordered hover responsive><thead><tr><th>Date</th><th>Task</th><th>Payout</th></tr></thead><tbody>{paymentHistory.length > 0 ? paymentHistory.map(p => (<tr key={p._id}><td>{new Date(p.createdAt).toLocaleDateString()}</td><td>{p.description.substring(0, 50)}...</td><td>${p.finalPrice || p.estimatedPrice}</td></tr>)) : <tr><td colSpan="3" className="text-center">No payment history.</td></tr>}</tbody></Table></div></Tab>
                </Tabs>
            </div>
        </div>
    );
};
export default ErranderDashboard;
