// frontend/src/pages/ErranderDashboard.js
import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert, Tabs, Tab, Table, Badge } from 'react-bootstrap';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import AuthContext from '../context/AuthContext';
import './ErranderDashboard.css';


// Reusable component for the statistics cards
const StatCard = ({ icon, title, value, colorClass }) => (
    <div className="stat-card h-100">
        <div className={`icon ${colorClass}`}>{icon}</div>
        <h4 className="fw-bold">{value}</h4>
        <p className="text-muted">{title}</p>
    </div>
);

// Component for displaying a single pending job
const AvailableJobCard = ({ task, onApply }) => (
    <Card className="mb-3 shadow-sm">
        <Card.Header as="h5">{task.service.name}</Card.Header>
        <Card.Body>
            <Card.Text>{task.description}</Card.Text>
            <Card.Text><strong>Estimated Payout:</strong> ${task.estimatedPrice}</Card.Text>
            <Button variant="success" onClick={() => onApply(task._id)}>View & Apply</Button>
        </Card.Body>
    </Card>
);

const MyTaskItem = ({ task, onUpdateStatus }) => (
     <Card className="mb-3 shadow-sm"><Card.Header className="d-flex justify-content-between align-items-center"><span>Task for User: ...{task.user._id.slice(-6)}</span><span className="fw-bold text-primary text-capitalize">{task.status.replace('_', ' ')}</span></Card.Header>
     <Card.Body>
        <Card.Text><strong>Description:</strong> {task.description}</Card.Text>
        {task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdateStatus(task._id, 'in_progress')}>Start Task</Button>}{task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdateStatus(task._id, 'completed')}>Mark as Complete</Button>}
    </Card.Body>
    </Card> 
);
