// frontend/src/services/taskService.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/tasks';

const createTask = (taskData, token) => { /* ... */ };
const assignTask = (taskId, erranderId, token) => axios.put(`${API_URL}/assign/${taskId}`, { erranderId }, { headers: { 'x-auth-token': token } });
const getAssignedTasks = (token) => axios.get(`${API_URL}/assigned`, { headers: { 'x-auth-token': token } });
const updateTaskStatus = (taskId, status, token) => axios.put(`${API_URL}/status/${taskId}`, { status }, { headers: { 'x-auth-token': token } });

// THIS IS THE CRITICAL FUNCTION FOR THE TRACKING PAGE
const getTaskDetails = (taskId, token) => {
    return axios.get(`${API_URL}/${taskId}`, {
        headers: { 'x-auth-token': token },
    });
};

const taskService = {
    createTask,
    assignTask,
    getAssignedTasks,
    updateTaskStatus,
    getTaskDetails, // Ensure it's exported
};

export default taskService;
