import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; 

// Context
import { AuthProvider } from './context/AuthContext.js';

// Components
import AppNavbar from './components/Navbar.js';
import PrivateRoute from './utils/PrivateRoute.js';

// Pages
import LoginPage from './pages/LoginPage.js';
import RegisterPage from './pages/RegisterPage.js';
import OtpPage from './pages/OtpPage.js';
import BecomeErranderPage from './pages/BecomeErranderPage.js';

// Placeholders
const HomePage = () => <div className="text-center mt-5"><h1>Welcome to the Errand Booking Platform</h1></div>;
const Dashboard = () => (
    <div className="text-center mt-5">
        <h1>User Dashboard</h1>
        <p>Your tasks and history will appear here.</p>
        <Link to="/become-errander" className="btn btn-lg submit-btn" style={{maxWidth: '300px', margin: 'auto'}}>
            Become an Errand Runner
        </Link>
    </div>
);
const ErrandersDashboard = () => <div className="text-center"><h1>Errander Dashboard</h1><p>Manage your profile, availability, and tasks.</p></div>;


function App() {
  return (
      <Router>
        <AuthProvider>
            <AppNavbar />
            <main className="container mt-4">
              <Routes>
                  {/* Public Routes */}
                  <Route path="/" element={<HomePage />} />
                  <Route path="/login" element={<LoginPage />} />
                  <Route path="/register" element={<RegisterPage />} />
                  <Route path="/verify-otp" element={<OtpPage />} />
                  
                  {/* Protected Routes */}
                  <Route element={<PrivateRoute roles={['user', 'errander']} />}>
                      <Route path="/dashboard" element={<Dashboard />} />
                      <Route path="/become-errander" element={<BecomeErranderPage />} />
                  </Route>

                  <Route element={<PrivateRoute roles={['errander']} />}>
                      <Route path="/errander-dashboard" element={<ErrandersDashboard />} />
                  </Route>
              </Routes>
            </main>
        </AuthProvider>
      </Router>
  );
}

export default App;
