import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert, Tabs, Tab, Table, Badge } from 'react-bootstrap';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import AuthContext from '../context/AuthContext';
import ChatWindow from '../components/ChatWindow'; // Import ChatWindow
import './ErranderDashboard.css';

const StatCard = ({ icon, title, value, colorClass }) => ( <div className="stat-card h-100"><div className={`icon ${colorClass}`}>{icon}</div><h4 className="fw-bold">{value}</h4><p className="text-muted">{title}</p></div> );
const AvailableJobCard = ({ task, onApply }) => ( <Card className="mb-3 shadow-sm"><Card.Header as="h5">{task.service.name}</Card.Header><Card.Body><Card.Text>{task.description}</Card.Text><Card.Text><strong>Est. Payout:</strong> ${task.estimatedPrice}</Card.Text><Button variant="success" onClick={() => onApply(task._id)}>Apply</Button></Card.Body></Card> );

const MyTaskItem = ({ task, onUpdateStatus, onOpenChat }) => (
    <Card className="mb-3 shadow-sm">
        <Card.Header className="d-flex justify-content-between align-items-center"><span>Task for User: ...{task.user._id.slice(-6)}</span><span className="fw-bold text-primary text-capitalize">{task.status.replace('_', ' ')}</span></Card.Header>
        <Card.Body>
            <Card.Text><strong>Description:</strong> {task.description}</Card.Text>
            <div className="d-flex gap-2">
                {task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdateStatus(task._id, 'in_progress')}>Start Task</Button>}
                {task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdateStatus(task._id, 'completed')}>Mark as Complete</Button>}
                <Button variant="outline-secondary" onClick={() => onOpenChat(task)}>Chat with User</Button>
            </div>
        </Card.Body>
    </Card>
);

const ErranderDashboard = () => {
    const { token } = useContext(AuthContext);
    const [summary, setSummary] = useState({ walletBalance: '0.00', jobsCompleted: 0, rating: '5.0' });
    const [myTasks, setMyTasks] = useState([]);
    const [showChat, setShowChat] = useState(false);
    const [selectedTaskForChat, setSelectedTaskForChat] = useState(null);
    // ... other states
    const [pendingTasks, setPendingTasks] = useState([]);
    const [paymentHistory, setPaymentHistory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchData = useCallback(async () => { /* ... same as before ... */ }, [token]);
    useEffect(() => { fetchData(); }, [fetchData]);

    const handleApplyForTask = async (taskId) => { /* ... same as before ... */ };
    const handleUpdateStatus = async (taskId, status) => { /* ... same as before ... */ };

    const handleOpenChat = (task) => {
        setSelectedTaskForChat(task);
        setShowChat(true);
    };

    if (loading) return <Container className="text-center mt-5"><Spinner /></Container>;

    const activeTasks = myTasks.filter(t => t.status !== 'completed' && t.status !== 'paid' && t.status !== 'cancelled');

    return (
        <>
            <div className="dashboard-container p-lg-4 p-md-3 p-2">
                <h2 className="mb-4 fw-bold">Errander Dashboard</h2>
                {/* ... The rest of the dashboard UI ... */}
                <Tabs defaultActiveKey="my-tasks" id="errander-tabs" className="mb-3" justify>
                    <Tab eventKey="available" title={<>Available Jobs <Badge bg="success">{pendingTasks.length}</Badge></>}>
                        {/* ... Available Jobs content ... */}
                    </Tab>
                    <Tab eventKey="my-tasks" title={<>My Jobs <Badge bg="primary">{activeTasks.length}</Badge></>}>
                        <div className="p-2">{activeTasks.length > 0 ? activeTasks.map(task => <MyTaskItem key={task._id} task={task} onUpdateStatus={handleUpdateStatus} onOpenChat={handleOpenChat} />) : <Alert variant="light">You have no active jobs.</Alert>}</div>
                    </Tab>
                    <Tab eventKey="payments" title="Payment History">
                        {/* ... Payment History content ... */}
                    </Tab>
                </Tabs>
            </div>
            {selectedTaskForChat && (
                <ChatWindow 
                    show={showChat}
                    handleClose={() => setShowChat(false)}
                    taskId={selectedTaskForChat._id}
                    recipientName={`User (...${selectedTaskForChat.user._id.slice(-6)})`}
                />
            )}
        </>
    );
};
export default ErranderDashboard;
