// frontend/src/services/taskService.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/tasks';

const createTask = (taskData, token) => {
    return axios.post(API_URL, taskData, {
        headers: { 'x-auth-token': token },
    });
};

const assignTask = (taskId, erranderId, token) => {
    return axios.put(`${API_URL}/assign/${taskId}`, { erranderId }, {
        headers: { 'x-auth-token': token },
    });
};

const getAssignedTasks = (token) => {
    return axios.get(`${API_URL}/assigned`, {
        headers: { 'x-auth-token': token },
    });
};

const updateTaskStatus = (taskId, status, token) => {
    return axios.put(`${API_URL}/status/${taskId}`, { status }, {
        headers: { 'x-auth-token': token },
    });
};

// THIS IS THE FIX: All functions must be exported here.
const taskService = {
    createTask,
    assignTask,
    getAssignedTasks,
    updateTaskStatus,
};

export default taskService;
