// frontend/src/pages/ErranderDashboard.js
import React, { useState, useEffect, useContext, useRef } from 'react';
import { Card, Button, Spinner, Alert, Row, Col, Tabs, Tab } from 'react-bootstrap';
import io from 'socket.io-client';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import AuthContext from '../context/AuthContext';
import './ErranderDashboard.css';

const SOCKET_URL = 'http://localhost:5000';

const ErranderDashboard = () => {
    const { token } = useContext(AuthContext);
    const [summary, setSummary] = useState({ walletBalance: 0, jobsCompleted: 0, rating: 5 });
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const socketRef = useRef(null);
    const locationIntervalRef = useRef(null);

    const fetchData = async () => {
        if (!token) {
            setLoading(false);
            return;
        }
        try {
            // Fetch all data in parallel
            const [summaryRes, tasksRes] = await Promise.all([
                erranderService.getDashboardSummary(token),
                taskService.getAssignedTasks(token)
            ]);
            setSummary(summaryRes.data);
            setTasks(tasksRes.data);
        } catch (err) {
            setError('Failed to load dashboard data.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        socketRef.current = io(SOCKET_URL);
        fetchData();
        return () => {
            socketRef.current.disconnect();
            if (locationIntervalRef.current) clearInterval(locationIntervalRef.current);
        };
    }, [token]);

    const handleUpdateStatus = async (taskId, status) => {
        try {
            const res = await taskService.updateTaskStatus(taskId, status, token);
            setTasks(tasks.map(t => t._id === taskId ? res.data : t));
            if (status === 'in_progress') startLocationSharing(taskId);
            if (status === 'completed') stopLocationSharing();
            // Refetch summary data after completing a task
            if (status === 'completed') await fetchData();
        } catch (err) {
            setError('Failed to update task status.');
        }
    };

    const startLocationSharing = (taskId) => {
        if (locationIntervalRef.current) clearInterval(locationIntervalRef.current);
        locationIntervalRef.current = setInterval(() => {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    socketRef.current.emit('updateLocation', { taskId, location: { latitude, longitude } });
                },
                (err) => console.error("Error getting location:", err),
                { enableHighAccuracy: true }
            );
        }, 5000);
    };

    const stopLocationSharing = () => {
        if (locationIntervalRef.current) clearInterval(locationIntervalRef.current);
    };

    if (loading) return <div className="text-center mt-5"><Spinner animation="border" /></div>;

    const activeTasks = tasks.filter(t => t.status !== 'completed' && t.status !== 'cancelled');
    const completedTasks = tasks.filter(t => t.status === 'completed');

    return (
        <div className="dashboard-container p-lg-4 p-md-3 p-2">
            <h2 className="mb-4 fw-bold">Errander Dashboard</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            <Row className="mb-4 g-3">
                <Col md={4}><StatCard icon="ðŸ’°" title="Wallet" value={`$${summary.walletBalance}`} colorClass="wallet-icon" /></Col>
                <Col md={4}><StatCard icon="ðŸ“¦" title="Jobs Done" value={summary.jobsCompleted} colorClass="jobs-icon" /></Col>
                <Col md={4}><StatCard icon="â­" title="Your Rating" value={summary.rating} colorClass="rating-icon" /></Col>
            </Row>
            <div className="tasks-section">
                <Tabs defaultActiveKey="active" id="task-tabs" className="mb-3" justify>
                    <Tab eventKey="active" title={<span className="fw-bold">Active Tasks <span className="badge bg-primary ms-1">{activeTasks.length}</span></span>}>
                        <div className="p-2">
                            {activeTasks.length > 0 ? activeTasks.map(task => <TaskItem key={task._id} task={task} onUpdate={handleUpdateStatus} />) : <Alert variant="light" className="text-center">You have no active tasks.</Alert>}
                        </div>
                    </Tab>
                    <Tab eventKey="completed" title={<span className="fw-bold">Completed <span className="badge bg-success ms-1">{completedTasks.length}</span></span>}>
                        <div className="p-2">
                            {completedTasks.length > 0 ? completedTasks.map(task => <TaskItem key={task._id} task={task} />) : <Alert variant="light" className="text-center">You haven't completed any tasks yet.</Alert>}
                        </div>
                    </Tab>
                </Tabs>
            </div>
        </div>
    );
};

const StatCard = ({ icon, title, value, colorClass }) => (
    <div className="stat-card h-100">
        <div className={`icon ${colorClass}`}>{icon}</div>
        <h4 className="fw-bold">{value}</h4>
        <p className="text-muted">{title}</p>
    </div>
);

const TaskItem = ({ task, onUpdate }) => (
    <Card className="task-card">
        <Card.Header className="task-card-header d-flex justify-content-between align-items-center">
            <span>Task ID: ...{task._id.slice(-6)}</span>
            <span className={`fw-bold text-capitalize text-primary`}>{task.status.replace('_', ' ')}</span>
        </Card.Header>
        <Card.Body>
            <Card.Text><strong>Description:</strong> {task.description}</Card.Text>
            <Card.Text className="text-muted"><strong>From:</strong> {task.pickupLocation.address}</Card.Text>
            <Card.Text className="text-muted"><strong>To:</strong> {task.dropoffLocation.address}</Card.Text>
            {onUpdate && task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdate(task._id, 'in_progress')}>Start Task</Button>}
            {onUpdate && task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdate(task._id, 'completed')}>Mark as Complete</Button>}
        </Card.Body>
    </Card>
);

export default ErranderDashboard;
