import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; 

// Context
import { AuthProvider } from './context/AuthContext.js';

// Components
import AppNavbar from './components/Navbar.js';
import PrivateRoute from './utils/PrivateRoute.js';

// Pages
import LoginPage from './pages/LoginPage.js';
import RegisterPage from './pages/RegisterPage.js';
import OtpPage from './pages/OtpPage.js';
// import ErrandTrackerPage from './pages/ErrandTrackerPage';
// import ErrandersDashboard from './pages/ErrandersDashboard';

// A simple placeholder for the home page
const HomePage = () => <div className="text-center"><h1>Welcome to the Errand Booking Platform</h1></div>;
const Dashboard = () => <div className="text-center"><h1>User Dashboard</h1><p>Your tasks and history will appear here.</p></div>;
const ErrandersDashboard = () => <div className="text-center"><h1>Errander Dashboard</h1><p>Manage your profile, availability, and tasks.</p></div>;


function App() {
  return (
      <Router>
        <AuthProvider>
            <AppNavbar />
            <Routes>
                {/* Public Routes */}
                <Route path="/" element={<HomePage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/verify-otp" element={<OtpPage />} />
                
                {/* Protected Routes */}
                <Route element={<PrivateRoute roles={['user', 'errander']} />}>
                    <Route path="/dashboard" element={<Dashboard />} />
                </Route>

                <Route element={<PrivateRoute roles={['errander']} />}>
                     <Route path="/errander-dashboard" element={<ErrandersDashboard />} />
                </Route>

                
                {/* <Route path="/track/:taskId" element={<ErrandTrackerPage />} /> */}
            </Routes>
        </AuthProvider>
      </Router>
  );
}

export default App;
