// frontend/src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    // Initialize token from localStorage
    const [token, setToken] = useState(() => localStorage.getItem('token'));
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    // This effect runs whenever the 'token' changes.
    // It's the single source of truth for decoding the token and setting the user state.
    useEffect(() => {
        if (token) {
            try {
                const decodedToken = jwt_decode(token);
                // Check if the token is expired
                if (decodedToken.exp * 1000 < Date.now()) {
                    // Token is expired, log the user out
                    logout();
                } else {
                    // Token is valid, set the user state
                    setUser(decodedToken.user);
                }
            } catch (error) {
                console.error("Invalid token found in storage:", error);
                // The token is invalid, log the user out
                logout();
            }
        } else {
            // No token, so no user
            setUser(null);
        }
        // Finished loading user data
        setLoading(false);

    }, [token]); // Re-run this effect whenever the token state changes

    // This function is called after successful OTP verification
    const loginAction = (newToken) => {
        localStorage.setItem('token', newToken);
        // This setToken call will trigger the useEffect hook above,
        // which will decode the token and set the user.
        setToken(newToken); 

        // Decode here just for immediate navigation
        const decoded = jwt_decode(newToken);
        if (decoded.user.isAdmin) {
            navigate('/admin');
        } else if (decoded.user.isErrander) {
            navigate('/errander-dashboard');
        } else {
            navigate('/dashboard');
        }
    };

    // This function clears the user session
    const logout = () => {
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
        navigate('/login');
    };
    
    const authContextValue = {
        token,
        user,
        loading,
        loginAction,
        logout,
    };

    return (
        <AuthContext.Provider value={authContextValue}>
            {!loading && children}
        </AuthContext.Provider>
    );
};

export default AuthContext;
