import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert, Tabs, Tab, Table, Badge } from 'react-bootstrap';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import AuthContext from '../context/AuthContext';
import './ErranderDashboard.css';

// Reusable component for the statistics cards
const StatCard = ({ icon, title, value, colorClass }) => ( <div className="stat-card h-100"><div className={`icon ${colorClass}`}>{icon}</div><h4 className="fw-bold">{value}</h4><p className="text-muted">{title}</p></div> );

// Component for displaying a single available job
const AvailableJobCard = ({ task, onApply }) => ( <Card className="mb-3 shadow-sm"><Card.Header as="h5">{task.service.name}</Card.Header><Card.Body><Card.Text>{task.description}</Card.Text><Card.Text><strong>Est. Payout:</strong> ${task.estimatedPrice}</Card.Text><Button variant="success" onClick={() => onApply(task._id)}>Apply</Button></Card.Body></Card> );

// Component for displaying an errander's own active/completed task
const MyTaskItem = ({ task, onUpdateStatus }) => (
    <Card className="mb-3 shadow-sm">
        <Card.Header className="d-flex justify-content-between align-items-center">
            <span>Task for User: ...{task.user._id.slice(-6)}</span>
            <span className="fw-bold text-primary text-capitalize">{task.status.replace('_', ' ')}</span>
        </Card.Header>
        <Card.Body>
            <Card.Text><strong>Description:</strong> {task.description}</Card.Text>
            {task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdateStatus(task._id, 'in_progress')}>Start Task</Button>}
            {task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdateStatus(task._id, 'completed')}>Mark as Complete</Button>}
        </Card.Body>
    </Card>
);

const ErranderDashboard = () => {
    const { token } = useContext(AuthContext);
    const [summary, setSummary] = useState({ walletBalance: '0.00', jobsCompleted: 0, rating: '5.0' });
    const [pendingTasks, setPendingTasks] = useState([]);
    const [myTasks, setMyTasks] = useState([]);
    const [paymentHistory, setPaymentHistory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchData = useCallback(async () => {
        if (!token) return;
        setLoading(true);
        try {
            const [summaryRes, pendingRes, myTasksRes, historyRes] = await Promise.all([
                erranderService.getDashboardSummary(token),
                taskService.getPendingTasks(token),
                taskService.getAssignedTasks(token),
                taskService.getPaymentHistory(token)
            ]);
            setSummary(summaryRes.data);
            setPendingTasks(pendingRes.data);
            setMyTasks(myTasksRes.data);
            setPaymentHistory(historyRes.data);
        } catch (err) { setError('Failed to load dashboard data.'); } 
        finally { setLoading(false); }
    }, [token]);

    useEffect(() => { fetchData(); }, [fetchData]);

    const handleApplyForTask = async (taskId) => {
        try {
            await taskService.assignTask(taskId, token);
            alert('You have successfully applied for the task!');
            fetchData(); // Refresh all data to move task from "Available" to "My Jobs"
        } catch (err) {
            alert('Could not apply for task. It may have already been taken.');
        }
    };

    const handleUpdateStatus = async (taskId, status) => { 
        try { 
            await taskService.updateTaskStatus(taskId, status, token); 
            fetchData(); // Refresh data to update status
        } catch (err) { 
            alert('Failed to update task status.'); 
        }
    };

    if (loading) return <Container className="text-center mt-5"><Spinner /></Container>;

    const activeAndAssignedTasks = myTasks.filter(t => t.status === 'accepted' || t.status === 'in_progress');
    
    return (
        <div className="dashboard-container p-lg-4 p-md-3 p-2">
            <h2 className="mb-4 fw-bold">Errander Dashboard</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            
            <Row className="mb-4 g-3">
                <Col md={4}><StatCard icon="ðŸ’°" title="Wallet" value={`$${summary.walletBalance}`} colorClass="wallet-icon" /></Col>
                <Col md={4}><StatCard icon="ðŸ“¦" title="Jobs Completed" value={summary.jobsCompleted} colorClass="jobs-icon" /></Col>
                <Col md={4}><StatCard icon="â­" title="Your Rating" value={summary.rating} colorClass="rating-icon" /></Col>
            </Row>

            <div className="tasks-section">
                 <Tabs defaultActiveKey="available" id="errander-tabs" className="mb-3" justify>
                    <Tab eventKey="available" title={<>Available Jobs <Badge bg="success">{pendingTasks.length}</Badge></>}>
                        <div className="p-2">{pendingTasks.length > 0 ? pendingTasks.map(task => <AvailableJobCard key={task._id} task={task} onApply={handleApplyForTask} />) : <Alert variant="light" className="text-center">No jobs available right now.</Alert>}</div>
                    </Tab>
                    <Tab eventKey="my-tasks" title={<>My Active Jobs <Badge bg="primary">{activeAndAssignedTasks.length}</Badge></>}>
                        <div className="p-2">{activeAndAssignedTasks.length > 0 ? activeAndAssignedTasks.map(task => <MyTaskItem key={task._id} task={task} onUpdateStatus={handleUpdateStatus} />) : <Alert variant="light" className="text-center">You have no active jobs.</Alert>}</div>
                    </Tab>
                    <Tab eventKey="payments" title="Payment History">
                         <div className="p-2">
                            <Table striped bordered hover responsive>
                                <thead><tr><th>Date</th><th>Task</th><th>Payout</th></tr></thead>
                                <tbody>{paymentHistory.length > 0 ? paymentHistory.map(p => (<tr key={p._id}><td>{new Date(p.createdAt).toLocaleDateString()}</td><td>{p.description.substring(0, 50)}...</td><td>${p.finalPrice || p.estimatedPrice}</td></tr>)) : <tr><td colSpan="3" className="text-center">No payment history.</td></tr>}</tbody>
                            </Table>
                         </div>
                    </Tab>
                </Tabs>
            </div>
        </div>
    );
};
export default ErranderDashboard;
