import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';

// Import your page components
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import AdminPage from './pages/AdminPage';
import ErranderDashboard from './pages/ErranderDashboard';
import OtpPage from './pages/OtpPage';

// FIX: Import the main CSS file for the application.
// This will apply your custom styles, including Tailwind CSS if configured.
import './index.css';

function App() {
  return (
    // The <Router> component must be the top-level parent
    // to provide routing context to all other components.
    <Router>
      <AuthProvider>
        <div className="App">
          <Routes>
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/admin" element={<AdminPage />} />
            <Route path="/dashboard" element={<ErranderDashboard />} />
            <Route path="/verify-otp" element={<OtpPage />} />
            
            {/* Default route to the login page */}
            <Route path="/" element={<LoginPage />} />
          </Routes>
        </div>
      </AuthProvider>
    </Router>
  );
}

export default App;
