// frontend/src/pages/ErranderDashboard.js
import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert, Tabs, Tab, Table, Badge } from 'react-bootstrap';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import AuthContext from '../context/AuthContext';
import './ErranderDashboard.css';


// Reusable component for the statistics cards
const StatCard = ({ icon, title, value, colorClass }) => (
    <div className="stat-card h-100">
        <div className={`icon ${colorClass}`}>{icon}</div>
        <h4 className="fw-bold">{value}</h4>
        <p className="text-muted">{title}</p>
    </div>
);

// Component for displaying a single pending job
const AvailableJobCard = ({ task, onApply }) => (
    <Card className="mb-3 shadow-sm">
        <Card.Header as="h5">{task.service.name}</Card.Header>
        <Card.Body>
            <Card.Text>{task.description}</Card.Text>
            <Card.Text><strong>Estimated Payout:</strong> ${task.estimatedPrice}</Card.Text>
            <Button variant="success" onClick={() => onApply(task._id)}>View & Apply</Button>
        </Card.Body>
    </Card>
);

const MyTaskItem = ({ task, onUpdateStatus }) => (
     <Card className="mb-3 shadow-sm"><Card.Header className="d-flex justify-content-between align-items-center"><span>Task for User: ...{task.user._id.slice(-6)}</span><span className="fw-bold text-primary text-capitalize">{task.status.replace('_', ' ')}</span></Card.Header>
     <Card.Body><Card.Text><strong>Description:</strong> {task.description}</Card.Text>{task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdateStatus(task._id, 'in_progress')}>Start Task</Button>}{task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdateStatus(task._id, 'completed')}>Mark as Complete</Button>}</Card.Body></Card> );


const ErranderDashboard = () => {
    const { user, token } = useContext(AuthContext);
    const [summary, setSummary] = useState({ walletBalance: '0.00', jobsCompleted: 0, rating: '5.0' });
    const [pendingTasks, setPendingTasks] = useState([]);
    const [myTasks, setMyTasks] = useState([]);
    const [paymentHistory, setPaymentHistory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchData = useCallback(async () => {
        if (!token) return;
        setLoading(true);
        try {
            const [summaryRes, pendingRes, myTasksRes, historyRes] = await Promise.all([
                erranderService.getDashboardSummary(token),
                taskService.getPendingTasks(token),
                taskService.getAssignedTasks(token),
                taskService.getPaymentHistory(token)
            ]);
            setSummary(summaryRes.data);
            setPendingTasks(pendingRes.data);
            setMyTasks(myTasksRes.data);
            setPaymentHistory(historyRes.data);
        } catch (err) {
            setError('Failed to load dashboard data. Please refresh the page.');
        } finally {
            setLoading(false);
        }
    }, [token]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleApplyForTask = async (taskId) => {
        // This logic needs to be double-checked against the user model.
        // Assuming the `user` object in the token has the `_id` of the *User*, but we need the *Errander* ID.
        // For now, this is a placeholder to demonstrate functionality.
        // In a real app, you might need another API call to get the errander ID from the user ID.
        try {
            // Placeholder: This assumes the user ID can be used to assign.
            // A more robust solution might be needed here.
            const erranderProfile = myTasks[0]?.errander; // A hacky way to get the errander ID if they have tasks
            if (!erranderProfile) {
                 alert('Could not determine your errander profile ID.');
                 return;
            }

            await taskService.assignTask(taskId, erranderProfile, token);
            alert('You have successfully applied for the task!');
            fetchData();
        } catch (err) {
            alert('Could not apply for task. It may have already been taken.');
        }
    };

    if (loading) return <Container className="text-center mt-5"><Spinner /></Container>;

    return (
        <div className="dashboard-container p-lg-4 p-md-3 p-2">
            <h2 className="mb-4 fw-bold">Errander Dashboard</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            
            <Row className="mb-4 g-3">
                <Col md={4}><StatCard icon="ðŸ’°" title="Wallet" value={`$${summary.walletBalance}`} colorClass="wallet-icon" /></Col>
                <Col md={4}><StatCard icon="ðŸ“¦" title="Jobs Completed" value={summary.jobsCompleted} colorClass="jobs-icon" /></Col>
                <Col md={4}><StatCard icon="â­" title="Your Rating" value={summary.rating} colorClass="rating-icon" /></Col>
            </Row>

            <div className="tasks-section">
                <Tabs defaultActiveKey="available" id="errander-tabs" className="mb-3" justify>
                    <Tab eventKey="available" title={<><span className="fw-bold">Available Jobs</span> <Badge bg="success">{pendingTasks.length}</Badge></>}>
                        <div className="p-2">
                            {pendingTasks.length > 0 ? pendingTasks.map(task => 
                                <AvailableJobCard key={task._id} task={task} onApply={handleApplyForTask} />
                            ) : <Alert variant="light" className="text-center">No jobs available right now. Check back soon!</Alert>}
                        </div>
                    </Tab>
                    <Tab eventKey="my-tasks" title={<><span className="fw-bold">My Jobs</span> <Badge bg="primary">{myTasks.filter(t => t.status !== 'completed').length}</Badge></>}>
                        <div className="p-2">
                           {myTasks.length > 0 ? <p>{myTasks.length} tasks assigned to you.</p> : <Alert variant="light" className="text-center">You have no active or completed jobs.</Alert>}
                        </div>
                    </Tab>
                    <Tab eventKey="payments" title={<><span className="fw-bold">Payment History</span></>}>
                         <div className="p-2">
                            <Table striped bordered hover responsive>
                                <thead>
                                    <tr><th>Date</th><th>Task Description</th><th>Payout</th></tr>
                                </thead>
                                <tbody>
                                    {paymentHistory.length > 0 ? paymentHistory.map(p => (
                                        <tr key={p._id}>
                                            <td>{new Date(p.createdAt).toLocaleDateString()}</td>
                                            <td>{p.description}</td>
                                            <td>${p.finalPrice || p.estimatedPrice}</td>
                                        </tr>
                                    )) : <tr><td colSpan="3" className="text-center">No payment history.</td></tr>}
                                </tbody>
                            </Table>
