import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert, Tabs, Tab, Table, Badge, Modal, Form, InputGroup } from 'react-bootstrap';
import taskService from '../services/taskService';
import erranderService from '../services/erranderService';
import withdrawalService from '../services/withdrawalService';
import AuthContext from '../context/AuthContext';
import ChatWindow from '../components/ChatWindow';
import './ErranderDashboard.css';

const StatCard = ({ icon, title, value, colorClass, onWithdraw }) => ( <div className="stat-card h-100"><div className={`icon ${colorClass}`}>{icon}</div><h4 className="fw-bold">{value}</h4><p className="text-muted">{title}</p>{onWithdraw && <Button variant="success" size="sm" className="mt-2" onClick={onWithdraw}>Withdraw Funds</Button>}</div> );
const AvailableJobCard = ({ task, onApply }) => ( <Card className="mb-3 shadow-sm"><Card.Header as="h5">{task.service.name}</Card.Header><Card.Body><Card.Text>{task.description}</Card.Text><Card.Text><strong>Est. Payout:</strong> ${task.estimatedPrice}</Card.Text><Button variant="success" onClick={() => onApply(task._id)}>Apply</Button></Card.Body></Card> );
const MyTaskItem = ({ task, onUpdateStatus, onOpenChat }) => ( <Card className="mb-3 shadow-sm"><Card.Header className="d-flex justify-content-between align-items-center"><span>Task for User: ...{task.user._id.slice(-6)}</span><span className="fw-bold text-primary text-capitalize">{task.status.replace('_', ' ')}</span></Card.Header><Card.Body><Card.Text><strong>Description:</strong> {task.description}</Card.Text><div className="d-flex gap-2">{task.status === 'accepted' && <Button variant="primary" onClick={() => onUpdateStatus(task._id, 'in_progress')}>Start Task</Button>}{task.status === 'in_progress' && <Button variant="success" onClick={() => onUpdateStatus(task._id, 'completed')}>Mark as Complete</Button>}<Button variant="outline-secondary" onClick={() => onOpenChat(task)}>Chat with User</Button></div></Card.Body></Card> );

const ErranderDashboard = () => {
    const { token } = useContext(AuthContext);
    const [summary, setSummary] = useState({ walletBalance: '0.00', jobsCompleted: 0, rating: '5.0' });
    const [myTasks, setMyTasks] = useState([]);
    const [showWithdrawModal, setShowWithdrawModal] = useState(false);
    const [withdrawAmount, setWithdrawAmount] = useState('');
    const [mpesaNumber, setMpesaNumber] = useState('');
    const [showChat, setShowChat] = useState(false);
    const [selectedTask, setSelectedTask] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const fetchData = useCallback(async () => { if (!token) return; setLoading(true); try { const [summaryRes, myTasksRes] = await Promise.all([erranderService.getDashboardSummary(token), taskService.getAssignedTasks(token)]); setSummary(summaryRes.data); setMyTasks(myTasksRes.data); } catch (err) { setError('Failed to load dashboard data.'); } finally { setLoading(false); }}, [token]);
    useEffect(() => { fetchData(); }, [fetchData]);
    const handleUpdateStatus = async (taskId, status) => { try { await taskService.updateTaskStatus(taskId, status, token); fetchData(); } catch (err) { alert('Failed to update task status.'); }};
    const handleOpenChat = (task) => { setSelectedTask(task); setShowChat(true); };
    const handleWithdraw = async (e) => {
        e.preventDefault();
        if (!withdrawAmount || !mpesaNumber) return alert('Please enter an amount and M-Pesa number.');
        try {
            await withdrawalService.requestWithdrawal({ amount: withdrawAmount, mpesaNumber }, token);
            alert('Withdrawal successful! Funds are on the way.');
            setShowWithdrawModal(false);
            setWithdrawAmount('');
            fetchData();
        } catch (err) { alert(err.response?.data?.msg || 'Withdrawal failed.'); }
    };

    if (loading) return <Container className="text-center mt-5"><Spinner /></Container>;
    const activeTasks = myTasks.filter(t => t.status !== 'completed' && t.status !== 'paid' && t.status !== 'cancelled');

    return (
        <>
            <div className="dashboard-container p-lg-4 p-md-3 p-2">
                <h2 className="mb-4 fw-bold">Errander Dashboard</h2>{error && <Alert variant="danger">{error}</Alert>}
                <Row className="mb-4 g-3"><Col md={4}><StatCard icon="💰" title="Wallet" value={`$${summary.walletBalance}`} colorClass="wallet-icon" onWithdraw={() => setShowWithdrawModal(true)} /></Col><Col md={4}><StatCard icon="📦" title="Jobs Completed" value={summary.jobsCompleted} colorClass="jobs-icon" /></Col><Col md={4}><StatCard icon="⭐" title="Your Rating" value={summary.rating} colorClass="rating-icon" /></Col></Row>
                <div className="tasks-section"><Tabs defaultActiveKey="my-tasks" id="errander-tabs" className="mb-3" justify>
                    <Tab eventKey="my-tasks" title={<>My Active Jobs <Badge bg="primary">{activeTasks.length}</Badge></>}><div className="p-2">{activeTasks.length > 0 ? activeTasks.map(task => <MyTaskItem key={task._id} task={task} onUpdateStatus={handleUpdateStatus} onOpenChat={handleOpenChat} />) : <Alert variant="light">You have no active jobs.</Alert>}</div></Tab>
                </Tabs></div>
            </div>
            {selectedTask && <ChatWindow show={showChat} handleClose={() => setShowChat(false)} taskId={selectedTask._id} recipientName={`User (...${selectedTask.user._id.slice(-6)})`}/>}
            <Modal show={showWithdrawModal} onHide={() => setShowWithdrawModal(false)} centered><Modal.Header closeButton><Modal.Title>Withdraw Funds to M-Pesa</Modal.Title></Modal.Header><Modal.Body><Form onSubmit={handleWithdraw}><p>Available Balance: <strong>${summary.walletBalance}</strong></p><Form.Group className="mb-3"><Form.Label>Amount to Withdraw</Form.Label><InputGroup><InputGroup.Text>$</InputGroup.Text><Form.Control type="number" placeholder="0.00" value={withdrawAmount} onChange={e => setWithdrawAmount(e.target.value)} required /></InputGroup></Form.Group><Form.Group className="mb-3"><Form.Label>M-Pesa Number</Form.Label><Form.Control type="tel" placeholder="e.g. 254712345678" value={mpesaNumber} onChange={e => setMpesaNumber(e.target.value)} required /></Form.Group><Button variant="primary" type="submit" className="w-100">Confirm Withdrawal</Button></Form></Modal.Body></Modal>
        </>
    );
};
export default ErranderDashboard;
