// frontend/src/pages/UserDashboard.js
import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Button, Container, Spinner, Alert, Card, Modal, Form } from 'react-bootstrap';
import taskService from '../services/taskService';
import AuthContext from '../context/AuthContext';

// A reusable Star Rating component
const StarRating = ({ rating, setRating }) => {
    return (
        <div className="my-3 text-center">
            {[...Array(5)].map((star, index) => {
                index += 1;
                return (
                    <button
                        type="button"
                        key={index}
                        className={index <= rating ? "btn text-warning" : "btn text-secondary"}
                        style={{ background: 'none', border: 'none', fontSize: '2.5rem', padding: '0 5px', boxShadow: 'none', outline: 'none' }}
                        onClick={() => setRating(index)}
                        onMouseEnter={() => { /* Optional: handle hover effect */ }}
                        onMouseLeave={() => { /* Optional: handle hover effect */ }}
                    >
                        <span className="star">★</span>
                    </button>
                );
            })}
        </div>
    );
};

const UserDashboard = () => {
    const { token } = useContext(AuthContext);
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [selectedTask, setSelectedTask] = useState(null);
    const [rating, setRating] = useState(0);
    const [feedback, setFeedback] = useState('');

    useEffect(() => {
        const fetchTasks = async () => {
            if (!token) return;
            try {
                const res = await taskService.getUserTasks(token);
                setTasks(res.data);
            } catch (err) {
                setError("Failed to fetch your task history.");
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchTasks();
    }, [token]);

    const handleShowModal = (task) => {
        setSelectedTask(task);
        setShowModal(true);
    };
    const handleCloseModal = () => {
        setShowModal(false);
        setRating(0);
        setFeedback('');
        setSelectedTask(null);
    };

    const handleRatingSubmit = async () => {
        if (rating === 0) {
            return alert('Please select at least one star.');
        }
        try {
            const res = await taskService.rateTask(selectedTask._id, { rating, feedback }, token);
            // Update the task in the list to show it has been rated
            setTasks(tasks.map(t => t._id === selectedTask._id ? res.data : t));
            handleCloseModal();
        } catch (err) {
            alert('Failed to submit rating. The task may have already been rated.');
            console.error(err);
        }
    };

    if (loading) return <Container className="text-center mt-5"><Spinner animation="border" variant="primary" /></Container>;

    return (
        <Container className="py-4">
            <div className="text-center mb-5">
                <h1 className="display-5 fw-bold">Your Dashboard</h1>
                <p className="lead text-muted">Book a new service or review your past errands.</p>
                <Link to="/services">
                    <Button 
                        variant="primary" 
                        size="lg" 
                        style={{
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
                            border: 'none'
                        }}
                    >
                        Book a New Service
                    </Button>
                </Link>
            </div>
            
            <h3 className="mb-3">Your Task History</h3>
            {error && <Alert variant="danger">{error}</Alert>}
            {tasks.length === 0 && !error ? (
                <Alert variant="light">You have no tasks yet. Book a service to get started!</Alert>
            ) : (
                tasks.map(task => (
                    <Card key={task._id} className="mb-3 shadow-sm">
                        <Card.Body>
                            <div className="d-flex justify-content-between align-items-center flex-wrap">
                                <div>
                                    <Card.Title>Task ID: ...{task._id.slice(-6)}</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">
                                        Errander: {task.errander ? `${task.errander.firstName} ${task.errander.lastName}` : 'Not Assigned Yet'}
                                    </Card.Subtitle>
                                    <Card.Text>
                                        <strong>Status:</strong> <span className="text-capitalize fw-bold">{task.status.replace('_', ' ')}</span>
                                    </Card.Text>
                                </div>
                                <div className="mt-2 mt-md-0">
                                    {task.status === 'completed' && !task.rating && (
                                        <Button variant="warning" onClick={() => handleShowModal(task)}>Rate Experience</Button>
                                    )}
                                    {task.rating > 0 && (
                                        <span className="text-warning fw-bold">You rated: {task.rating} ★</span>
                                    )}
                                </div>
                            </div>
                        </Card.Body>
                    </Card>
                ))
            )}

            {/* Rating Modal */}
            <Modal show={showModal} onHide={handleCloseModal} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Rate Your Experience</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p className="text-center">How was your experience with <strong>{selectedTask?.errander?.firstName}</strong> for task "...{selectedTask?._id.slice(-6)}"?</p>
                    <StarRating rating={rating} setRating={setRating} />
                    <Form.Control 
                        as="textarea" 
                        rows={3} 
                        placeholder="Optional: Leave a comment about your experience..." 
                        value={feedback} 
                        onChange={(e) => setFeedback(e.target.value)} 
                    />
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>Cancel</Button>
                    <Button variant="primary" onClick={handleRatingSubmit}>Submit Rating</Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
};

export default UserDashboard;
