import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; 

// Context & Components
import { AuthProvider } from './context/AuthContext.js';
import AppNavbar from './components/Navbar.js';
import PrivateRoute from './utils/PrivateRoute.js';

// Pages
import HomePage from './pages/HomePage.js'; // Assuming this exists
import LoginPage from './pages/LoginPage.js';
import RegisterPage from './pages/RegisterPage.js';
import OtpPage from './pages/OtpPage.js';
import UserDashboard from './pages/UserDashboard.js'; // The new user dashboard
import ErranderDashboard from './pages/ErranderDashboard.js'; // The real errander dashboard
import BecomeErranderPage from './pages/BecomeErranderPage.js';
import AdminPage from './pages/AdminPage.js';
import ServicesPage from './pages/ServicesPage.js';
import BookingPage from './pages/BookingPage.js';
import FindErranderPage from './pages/FindErranderPage.js';

function App() {
  return (
      <Router>
        <AuthProvider>
            <AppNavbar />
            <main className="container-fluid p-0">
              <Routes>
                  {/* Public Routes */}
                  <Route path="/" element={<HomePage />} />
                  <Route path="/login" element={<LoginPage />} />
                  <Route path="/register" element={<RegisterPage />} />
                  <Route path="/verify-otp" element={<OtpPage />} />
                  
                  {/* --- CORRECTED USER & ERRANDER ROUTES --- */}
                  <Route element={<PrivateRoute />}>
                      {/* Routes for REGULAR USERS */}
                      <Route path="/dashboard" element={<UserDashboard />} />
                      <Route path="/services" element={<ServicesPage />} />
                      <Route path="/booking/:serviceId" element={<BookingPage />} />
                      <Route path="/find-errander/:taskId" element={<FindErranderPage />} />
                      <Route path="/become-errander" element={<BecomeErranderPage />} />
                      
                      {/* Route for ERRANDERS */}
                      <Route path="/errander-dashboard" element={<ErranderDashboard />} />
                  </Route>

                  {/* Admin Routes */}
                  <Route element={<PrivateRoute isAdminRoute={true} />}>
                      <Route path="/admin" element={<AdminPage />} />
                  </Route>
              </Routes>
            </main>
        </AuthProvider>
      </Router>
  );
}

// Simple HomePage placeholder if it doesn't exist
const HomePageComponent = () => <div className="text-center mt-5"><h1>Welcome to the Errand Booking Platform</h1></div>;
if (!HomePage) {
    // eslint-disable-next-line no-const-assign
    HomePage = HomePageComponent;
}

export default App;
