// frontend/src/components/Navbar.js
import React, { useContext } from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import AuthContext from '../context/AuthContext';

const AppNavbar = () => {
    const { user, logout } = useContext(AuthContext);

    return (
        <Navbar bg="light" expand="lg" className="mb-4 shadow-sm">
            {/* --- TEMPORARY DEBUGGER --- */}
            <div style={{ position: 'fixed', top: '80px', left: '20px', background: 'lightyellow', border: '1px solid black', padding: '10px', zIndex: 9999, borderRadius: '10px' }}>
                <pre style={{ margin: 0, fontSize: '12px' }}>
                    <strong>DEBUG - User State:</strong>
                    <br />
                    {user ? JSON.stringify(user, null, 2) : 'null'}
                </pre>
            </div>
             {/* --- END DEBUGGER --- */}

            <Container>
                <LinkContainer to="/">
                    <Navbar.Brand className="fw-bold" style={{color: '#667eea'}}>Errander</Navbar.Brand>
                </LinkContainer>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ms-auto align-items-center">
                        {user ? (
                            <>
                                <LinkContainer to="/dashboard">
                                    <Nav.Link>Dashboard</Nav.Link>
                                </LinkContainer>
                                {user.isAdmin && (
                                    <LinkContainer to="/admin">
                                        <Nav.Link>Admin Panel</Nav.Link>
                                    </LinkContainer>
                                )}
                                <Button variant="outline-danger" onClick={logout} size="sm">Logout</Button>
                            </>
                        ) : (
                            <>
                                <LinkContainer to="/login">
                                    <Nav.Link>Login</Nav.Link>
                                </LinkContainer>
                                <LinkContainer to="/register">
                                     <Nav.Link as={Button} variant="primary" className="ms-2 text-white" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', border: 'none'}}>Sign Up</Nav.Link>
                                </LinkContainer>
                            </>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default AppNavbar;
