import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Input, Statistic } from 'antd';
import withdrawalService from '../services/withdrawalService';
import taskService from '../services/taskService';
import { useNavigate } from 'react-router-dom';

const ErranderDashboard = () => {
    const [balance, setBalance] = useState(0);
    const [tasksCompleted, setTasksCompleted] = useState(0);
    const [loading, setLoading] = useState(true);
    
    // Original state variables that were unused
    const [withdrawAmount, setWithdrawAmount] = useState('');
    const [mpesaNumber, setMpesaNumber] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                setLoading(true);
                const balanceRes = await taskService.getBalance();
                setBalance(balanceRes.data.balance);
                const tasksRes = await taskService.getCompletedTasks();
                setTasksCompleted(tasksRes.data.count);
            } catch (error) {
                console.error("Failed to fetch dashboard data", error);
            } finally {
                setLoading(false);
            }
        };

        fetchDashboardData();
    }, []);

    // Original handler that was unused
    const handleWithdraw = async () => {
        try {
            await withdrawalService.withdraw({ amount: withdrawAmount, mpesaNumber });
            // Show success message
        } catch (error) {
            // Show error message
        }
    };

    return (
        <div className="p-4 md:p-8 bg-gray-50 min-h-screen">
            <h1 className="text-3xl font-bold text-gray-800 mb-6">My Dashboard</h1>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                <Card loading={loading}>
                    <Statistic title="Current Balance" value={balance} prefix="Ksh" precision={2} />
                </Card>
                <Card loading={loading}>
                    <Statistic title="Tasks Completed" value={tasksCompleted} />
                </Card>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <Card title="Withdraw Funds">
                    <Form onFinish={handleWithdraw} layout="vertical">
                        <Form.Item label="Amount to Withdraw" required>
                            <Input
                                type="number"
                                value={withdrawAmount}
                                onChange={(e) => setWithdrawAmount(e.target.value)}
                                placeholder="e.g., 500"
                            />
                        </Form.Item>
                        <Form.Item label="M-Pesa Number" required>
                            <Input
                                value={mpesaNumber}
                                onChange={(e) => setMpesaNumber(e.target.value)}
                                placeholder="e.g., 254712345678"
                            />
                        </Form.Item>
                        <Form.Item>
                            <Button type="primary" htmlType="submit">
                                Request Withdrawal
                            </Button>
                        </Form.Item>
                    </Form>
                </Card>
                <Card title="Find Work">
                     <Button type="primary" size="large" onClick={() => navigate('/tasks')}>
                        Find New Tasks
                    </Button>
                </Card>
            </div>
        </div>
    );
};

export default ErranderDashboard;
