// frontend/src/services/taskService.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/tasks';

// This object will hold all our service functions
const taskService = {
    createTask: (taskData, token) => {
        return axios.post(API_URL, taskData, {
            headers: { 'x-auth-token': token },
        });
    },

    assignTask: (taskId, erranderId, token) => {
        return axios.put(`${API_URL}/assign/${taskId}`, { erranderId }, {
            headers: { 'x-auth-token': token },
        });
    },
    
    getAssignedTasks: (token) => {
        return axios.get(`${API_URL}/assigned`, {
            headers: { 'x-auth-token': token },
        });
    },

    updateTaskStatus: (taskId, status, token) => {
        return axios.put(`${API_URL}/status/${taskId}`, { status }, {
            headers: { 'x-auth-token': token },
        });
    },
    
    getTaskDetails: (taskId, token) => {
        return axios.get(`${API_URL}/${taskId}`, {
            headers: { 'x-auth-token': token },
        });
    },

    // --- NEW FUNCTION TO GET A USER'S TASK HISTORY ---
    getUserTasks: (token) => {
        return axios.get(`${API_URL}/user/history`, {
            headers: { 'x-auth-token': token },
        });
    },

    // --- NEW FUNCTION TO SUBMIT A RATING ---
    rateTask: (taskId, ratingData, token) => {
        // THE FIX IS HERE: Corrected API__URL to API_URL
        return axios.put(`${API_URL}/rate/${taskId}`, ratingData, {
            headers: { 'x-auth-token': token },
        });
    }
};

export default taskService;
